// Avoid `console` errors in browsers that lack a console.
(function() {
    var method;
    var noop = function () {};
    var methods = [
        'assert', 'clear', 'count', 'debug', 'dir', 'dirxml', 'error',
        'exception', 'group', 'groupCollapsed', 'groupEnd', 'info', 'log',
        'markTimeline', 'profile', 'profileEnd', 'table', 'time', 'timeEnd',
        'timeStamp', 'trace', 'warn'
    ];
    var length = methods.length;
    var console = (window.console = window.console || {});

    while (length--) {
        method = methods[length];

        // Only stub undefined methods.
        if (!console[method]) {
            console[method] = noop;
        }
    }
}());

// Wrap first letter plugin
(function($){

  $.fn.wrapFirstLetter = function(options) {

    var settings = {
      'charpattern' : /[a-z0-9"]/i,
      'tag'         : 'span',
      'class'       : 'o-dropcap u-theme--background-color--base'
    };

    if (options) {
        jQuery.extend(true, settings, options);
    }

    function _wrap(content) {
      var len = content.length;
      var intag = false;

      for (var i=0; i<len; i++) {
        if (content[i] == '<') {
          intag = true;
          continue;
        }

        if (content[i] == '>') {
          intag = false;
          continue;
        }

        if (intag) {
          continue;
        }




        if (settings.charpattern.test(content[i])) {
          var pre = '';
          var post = '';
          var t = settings.tag;
          var c = settings['class'];
          console.log(content[i]);
          if (content[i] == '"') {
            var letter = '&ldquo;';
            var c = 'o-dropcap o-dropcap__quote u-theme--background-color--base'
          } else {
            var letter = content[i];
          }
          if (i==0) {
            post = content.substring(1);
            return pre + '<'+t+' class=\'' + c + '\' data-letter='+letter+'></'+t+'>' + post;
          }
          if (i==(len-1)) {
            pre = content.substring(0,i);
            return pre + '<'+t+' class=\'' + c + '\' data-letter='+letter+'></'+t+'>' + post;
          }

          pre = content.substring(0,i);
          post = content.substring(i+1);
          return pre + '<'+t+' class=\'' + c + '\' data-letter='+letter+'></'+t+'>' + post;
        }
      }
    }

    return this.each(function() {
      var obj = $(this);
      var content = obj.html();
      var newcontent = _wrap(content);
      obj.html(newcontent);
    });

  };

})(jQuery);

